/**
 * Turn virtual files into a ESLint results that can be passed directly to an
 * ESLint formatter.
 *
 * @param {Array<VFile>} files
 *   Virtual files.
 * @returns {Array<LintResult>}
 *   Lint results.
 */
export function toESLint(files: Array<VFile>): Array<LintResult>
export type LintResult = import('eslint').ESLint.LintResult
export type LintMessage = import('eslint').Linter.LintMessage
export type VFile = import('vfile').VFile
export type VFileMessage = import('vfile-message').VFileMessage
